const mongoose  = require("mongoose")

const userSchema = new mongoose.Schema({
username:{
    type:String,
    required:true,
    unique:true
},
email:{
    type:String,
    required:true
},
password:{
    type:String,
    required:true
},
image:{
    type:String,
    default:"data:image/png;base64,"
}, 
isAdmin:{
    type:Boolean,
    default:false
}
}, {timestamps:true})

module.exports = mongoose.model("User",userSchema)